import React from "react"
import boxes from "./boxes"
import './App.css';
import Box from "./Box"

function App() {
  const [box, setBox] = React.useState(boxes)
  function toggle(){
    setBox(prevOn => !prevOn)
}
  const boxesMap = box.map((item)=>(<Box className="box" key={item.id} on={item.on} func ={setBox} toggle={toggle}/>))
  return (
    <div className="boxes">
      {boxesMap}
    </div>
  )
}

export default App;
