import React from "react"
import boxes from "./boxes"
import './App.css';
import Box from "./Box"

function App() {
  const [box, setBox] = React.useState(boxes)
  function toggle(id){
    setBox(prevBox =>{
      const newBox = []
      for(let i=0;i<prevBox.length;i++){
        const currentBox = prevBox[i];
        if(currentBox.id === id){
          const updatedBox = {
            ...currentBox,
            on: !currentBox.on
          }
          newBox.push(updatedBox)
        }else{
          newBox.push(currentBox)
        }
      }
      return newBox
    })
}
  const boxesMap = box.map((item)=>(<Box className="box" key={item.id} on={item.on} func ={setBox} toggle={toggle}/>))
  return (
    <div className="boxes">
      {boxesMap}
    </div>
  )
}

export default App;
