    const [selected, setSelected] = React.useState({
        answer: "",
        id:"",
        isSelected:""
    })

    console.log(options)
    const optionValues = options.map(val=> {
        return <h5 className="options-info">{val}</h5>
    })

    

   return (
    <div className="questions-block">
        <h3 className="que">{props.data.question}</h3>
        <div className="options">
        {optionValues}
        </div>
        <hr/>
    </div>
   )
}
