import React from "react";

export default function Quiz({ data, id, selectedOption, handleClick }) {
    const options = [...data.incorrect_answers, data.correct_answer];

    const optionValues = options.map((val, index) => {
        const isSelected = selectedOption === val;
        const styles = {
            backgroundColor: isSelected ? "lightgreen" : "white"
        };
        return (
            <div 
                key={index} 
                className="options-info" 
                onClick={() => handleClick(id, val)} 
                style={styles}
            >
                {val}
            </div>
        );
    });

    return (
        <div className="questions-block">
            <h3 className="que">{data.question}</h3>
            <div className="options">
                {optionValues}
            </div>
            <hr />
        </div>
    );
}
