import React, { useEffect } from "react";

export default function Quiz(props){
    const styles={
        backgroundColor : props.status ? "green": "gray"
    }
        var incorrectAnswers = []
        const correctAnswer = props.data.correct_answer
    function updateAnswer(){
        incorrectAnswers = props.data.incorrect_answers
    incorrectAnswers.push(correctAnswer)
    }
    
    updateAnswer();


    console.log(incorrectAnswers)
    const optionValues = incorrectAnswers.map(val=> {
        return <div className="options-info" onClick={selectOption(props.key)}>{val}</div>
    })
    
    

   return (
    <div className="questions-block">
        <h3 className="que">{props.data.question}</h3>
        <div className="options">
        {optionValues}
        </div>
        <hr/>
    </div>
   )
}
