import React from "react";

export default function Quiz({ data, id, status, handleClick }) {
    const styles = {
        backgroundColor: status ? "green" : "gray"
    };
    const options = [...data.incorrect_answers, data.correct_answer];

    const optionValues = options.map((val, index) => {
        return <div key={index} className="options-info" onClick={() => handleClick(id)}>{val}</div>;
    });

    return (
        <div className="questions-block" style={styles}>
            <h3 className="que">{data.question}</h3>
            <div className="options">
                {optionValues}
            </div>
            <hr />
        </div>
    );
}
