import React, { useState, useEffect } from "react";
import Quiz from "../components/Quiz";
import { nanoid } from "nanoid";

export default function Home() {
    const [quizData, setQuizData] = useState([]);
    const [questions, setQuestions] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await fetch("https://opentdb.com/api.php?amount=50&category=9&difficulty=easy&type=multiple");
                if (!response.ok) {
                    if (response.status === 429) {
                        throw new Error("Rate limit exceeded. Please try again later.");
                    } else {
                        throw new Error(`Error: ${response.status}`);
                    }
                }
                const data = await response.json();
                setQuestions(data.results);
            } catch (err) {
                setError(err.message);
                console.error('Error fetching questions:', err);
            }
        };
        fetchQuestions();
    }, []);

    useEffect(() => {
        if (questions.length > 0) {
            const arr = [];
            for (let i = 0; i < 5; i++) {
                arr.push({
                    fullData: questions[Math.floor(Math.random() * questions.length)],
                    selectedOption: null,
                    id: nanoid()
                });
            }
            setQuizData(arr);
        }
    }, [questions]);

    function handleClick(quizId, option) {
        setQuizData(prevData => prevData.map(val => {
            return val.id === quizId ?
                { ...val, selectedOption: option } : val
        }));
    }

    const elements = quizData.map(val => {
        return <Quiz 
            key={val.id} 
            data={val.fullData} 
            id={val.id} 
            selectedOption={val.selectedOption} 
            handleClick={handleClick} 
        />;
    });

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="home-container">
            <div className="questions-container">
                {elements}
            </div>
        </div>
    );
}
