import React from "react"
import "./App.css"
import Die from "./Die"
import {nanoid} from "nanoid"

export default function App(){
  const [dice, setDice] = React.useState(allNewDice())
  function allNewDice(){
  const arr = []
  for(let i=0;i<10;i++){
    arr.push({
      value : Math.ceil(Math.random()*6 ), 
      isHeld: false,
      id: nanoid()
    })
  }
  return arr
}
    function handleChange(id){
        setDice(prevDice => {
          return prevDice.map((val)=>{
            return val.id === id ? {...val, isHeld: !val.isHeld} : val
          })
        })
    }
  const diceValues = dice.map((val)=>{
    return <Die value={val.value} key = {val.id} onClick={handleChange(val.id)}/>
  })

  function rollDice(){
    setDice(allNewDice())
  }
  return (
    <div className="container">
      <main className="main">
        <div className="dice-container">
            {diceValues}
          </div>
          <button onClick={rollDice}>Reset</button>
      </main>
    </div>
  )
}
