import React from "react"
import "./App.css"
import Die from "./Die"
import {nanoid} from "nanoid"

export default function App(){
  const [dice, setDice] = React.useState(allNewDice())
  const [tenzie, setTenzie] = React.useState(false)

  React.useEffect(() => {
    const allHeld = dice.every(die => die.isHeld)
    const value = dice[0].value
    const allSame = dice.every(die => die.value === value)
    if(allHeld && allSame){
    setTenzie(true)
    }
  },[dice])


  function generateNewDie() {
    return {
      value : Math.ceil(Math.random()*6 ), 
      isHeld: false,
      id: nanoid()
    }
  }


  function allNewDice(){
  const arr = []
  for(let i=0;i<10;i++){
    arr.push(generateNewDie())
  }
  return arr
}


  function rollDice(){
    setDice(oldDice => oldDice.map(die => {
      return die.isHeld?
        die :
        generateNewDie()
    }))
  }

  function holdDice(id){
    setDice(oldDice => oldDice.map(die =>{
      return die.id === id ? 
      {...die, isHeld: !die.isHeld} : 
      die
    }))
  }

  const diceValues = dice.map((val)=>{
    return <Die value={val.value} key = {val.id} isHeld = {val.isHeld} holdDice = {() => holdDice(val.id)}/>
  })
  const styles = {
    backgroundColor : tenzie ? "Green" : "red",
    color: tenzie ? "white" : "black"
  }

  return (
    <div className="container">
      <main className="main">
        <div className="game-info">
        <h1>Tenzies</h1>
        <p>Roll until all dice are the same. Click
          each die to freeze it at its current value between rolls.
        </p>
        </div>
        <div className="dice-container">
            {diceValues}
          </div>
          <button style={styles} onClick={rollDice}>{tenzie ? "You Won!!!" : "Roll"}</button>
      </main>
    </div>
  )
}
